    	var evts = JSON.parse( $("#holydays").val());
    	var selectedDate = $("#today").val();
    	$(document).ready(function() {

    		$('#calendar').fullCalendar({
    			header: {
    				left: '',
    				center: 'prev, title ,next',
    				right:  ''
    				
    			},
    			//: true, 
    			//views: { agenda: { eventLimit: 1 } },
    			defaultDate: selectedDate,
    			defaultView: 'year',
    			yearColumns: 4,
    			selectable: true,
    			//selectHelper: false,
    			select: function(start, end, jsEvent, view) {
    				var title = prompt('제목:');
    				var eventData;
    				if (title) {
    					eventData = {
    						title: title,
    						start: start,
    						color:"#fec107",
    						type:"CUSTOM",
    						allDay:true
    					};
    				
	    				$.ajax({
	    					url : "/settings/holyday/edit",
	    					data: { title: title, year:$("#currentYear").val(),year:start.format("YYYY"), date:start.format("YYYY-MM-DD"),type:"CUSTOM" },
	    					method : "put",
	    					success : function(result) {
	    						
	    						eventData.id = result.id;
	    						eventData.date = result.date;
	    						$('#calendar').fullCalendar('renderEvent', eventData, true); // stick? = true
	    						alert("등록되었습니다.");
	    					},
	    					error : function(data) {
	    						console.error("Update snapshot", data);
	    					}
	    			    });
    				}
    				$('#calendar').fullCalendar('unselect');
    				
    			},
    			editable: true	,
    			
    			eventClick: function(calEvent,jsEvent, view) {
    				
    				$("#id").val(calEvent.id);
    				$("#title").val(calEvent.title);
    				$("#date").val(calEvent.date);
    				$("#responsive-modal").modal();

    				
    			},
    			events: evts
    		});

    		
    	});
